import React, { useState, useEffect } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { useAuth } from "@/contexts/AuthContext";

const defaultWidgets = [
  { id: "leads", label: "Total Leads" },
  { id: "clients", label: "Total Clients" },
  { id: "revenue", label: "Revenue" },
  { id: "campaigns", label: "Active Campaigns" },
  { id: "calendar", label: "Calendar" },
  { id: "analytics", label: "Analytics" },
];

const DashboardCustomizer = ({ children, widgetComponents }) => {
  const { db, user, userId } = useAuth();
  const [widgets, setWidgets] = useState([]);
  const [visible, setVisible] = useState({});
  const [allowed, setAllowed] = useState(null);
  const isAdmin = user?.role === 'admin';

  // Fetch allowed widgets from Firestore
  useEffect(() => {
    if (!db || !userId) return;
    let unsub;
    (async () => {
      const { doc, onSnapshot } = await import('firebase/firestore');
      unsub = onSnapshot(doc(db, 'users', userId), (docSnap) => {
        setAllowed(docSnap.exists() ? docSnap.data().allowedWidgets : null);
      });
    })();
    return () => unsub && unsub();
  }, [db, userId]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("dashboardLayout"));
    if (saved) {
      setWidgets(saved.widgets);
      setVisible(saved.visible);
    } else {
      setWidgets(defaultWidgets);
      setVisible(Object.fromEntries(defaultWidgets.map(w => [w.id, true])));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("dashboardLayout", JSON.stringify({ widgets, visible }));
  }, [widgets, visible]);

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const reordered = Array.from(widgets);
    const [removed] = reordered.splice(result.source.index, 1);
    reordered.splice(result.destination.index, 0, removed);
    setWidgets(reordered);
  };

  // Only show toggles for widgets allowed by admin (or all if admin)
  const toggleableWidgets = isAdmin ? widgets : widgets.filter(w => !allowed || allowed.includes(w.id));

  return (
    <div>
      <div className="mb-4 flex flex-wrap gap-4">
        {toggleableWidgets.map(w => (
          <label key={w.id} className="flex items-center gap-2">
            <input
              type="checkbox"
              checked={visible[w.id]}
              onChange={e => setVisible(v => ({ ...v, [w.id]: e.target.checked }))}
              disabled={!isAdmin && allowed && !allowed.includes(w.id)}
            />
            {w.label}
          </label>
        ))}
      </div>
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="dashboard-widgets" direction="horizontal">
          {(provided) => (
            <div ref={provided.innerRef} {...provided.droppableProps} className="flex flex-wrap gap-8 mb-12">
              {widgets.map((w, i) =>
                visible[w.id] && widgetComponents[w.id] ? (
                  <Draggable key={w.id} draggableId={w.id} index={i}>
                    {(prov) => (
                      <div ref={prov.innerRef} {...prov.draggableProps} {...prov.dragHandleProps}>
                        {widgetComponents[w.id]()}
                      </div>
                    )}
                  </Draggable>
                ) : null
              )}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default DashboardCustomizer;

import React, { useState, useEffect } from "react";
import { db } from "@/lib/firebase";
import { collection, query, onSnapshot, addDoc } from 'firebase/firestore';
import { demoDisputes } from "../data/demoData";
import jsPDF from "jspdf";

export default function DisputeCenter() {
  const [disputes, setDisputes] = useState(demoDisputes);
  const [useDemo, setUseDemo] = useState(true);
  const [form, setForm] = useState({
    clientName: "",
    bureau: "Experian",
    type: "Credit Report Error",
    status: "Open",
    reason: "Incorrect info",
    notes: "",
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    let unsubscribe = null;
    try {
      const q = query(collection(db, 'disputes'));
      unsubscribe = onSnapshot(q,
        (snapshot) => {
          const firebaseData = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
          setDisputes(firebaseData.length ? firebaseData : demoDisputes);
          setUseDemo(firebaseData.length === 0);
        },
        (error) => {
          setDisputes(demoDisputes);
          setUseDemo(true);
        }
      );
    } catch (error) {
      setDisputes(demoDisputes);
      setUseDemo(true);
    }
    return () => unsubscribe && unsubscribe();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAddDispute = async (e) => {
    e.preventDefault();
    setLoading(true);
    const newDispute = {
      ...form,
      dateCreated: new Date().toISOString().slice(0, 10),
      id: Math.random().toString(36).slice(2, 10),
    };
    try {
      if (!useDemo) {
        await addDoc(collection(db, 'disputes'), newDispute);
      }
      setDisputes([newDispute, ...disputes]);
      setForm({
        clientName: "",
        bureau: "Experian",
        type: "Credit Report Error",
        status: "Open",
        reason: "Incorrect info",
        notes: "",
      });
    } finally {
      setLoading(false);
    }
  };

  const exportPDF = () => {
    const doc = new jsPDF();
    doc.text("Disputes Export", 10, 10);
    disputes.forEach((d, i) => {
      doc.text(
        `${i + 1}. ${d.clientName} | ${d.bureau} | ${d.type} | ${d.status} | ${d.dateCreated}`,
        10,
        20 + i * 10
      );
    });
    doc.save("disputes.pdf");
  };

  return (
    <div className="max-w-3xl mx-auto p-4 space-y-8">
      <h1 className="text-2xl font-bold mb-2">Dispute Center</h1>
      {/* Dispute Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
        <div className="bg-blue-100 rounded p-4 text-center">
          <div className="text-lg font-semibold text-blue-700">Open</div>
          <div className="text-2xl font-bold">{disputes.filter((d) => d.status === "Open").length}</div>
        </div>
        <div className="bg-green-100 rounded p-4 text-center">
          <div className="text-lg font-semibold text-green-700">Resolved</div>
          <div className="text-2xl font-bold">{disputes.filter((d) => d.status === "Resolved").length}</div>
        </div>
        <div className="bg-yellow-100 rounded p-4 text-center">
          <div className="text-lg font-semibold text-yellow-700">In Progress</div>
          <div className="text-2xl font-bold">{disputes.filter((d) => d.status === "In Progress").length}</div>
        </div>
        <div className="bg-gray-100 rounded p-4 text-center">
          <div className="text-lg font-semibold text-gray-700">Total</div>
          <div className="text-2xl font-bold">{disputes.length}</div>
        </div>
      </div>
      {/* Add Dispute Form */}
      <form onSubmit={handleAddDispute} className="bg-gray-50 rounded p-4 mb-6 space-y-2">
        <div className="flex flex-col md:flex-row gap-2">
          <input name="clientName" value={form.clientName} onChange={handleChange} required placeholder="Client Name" className="border p-2 rounded flex-1" />
          <select name="bureau" value={form.bureau} onChange={handleChange} className="border p-2 rounded">
            <option>Experian</option>
            <option>Equifax</option>
            <option>TransUnion</option>
          </select>
          <select name="type" value={form.type} onChange={handleChange} className="border p-2 rounded">
            <option>Credit Report Error</option>
            <option>Identity Theft</option>
            <option>Other</option>
          </select>
          <select name="status" value={form.status} onChange={handleChange} className="border p-2 rounded">
            <option>Open</option>
            <option>In Progress</option>
            <option>Resolved</option>
          </select>
        </div>
        <div className="flex flex-col md:flex-row gap-2">
          <input name="reason" value={form.reason} onChange={handleChange} required placeholder="Reason Code/Description" className="border p-2 rounded flex-1" />
          <input name="notes" value={form.notes} onChange={handleChange} placeholder="Notes" className="border p-2 rounded flex-1" />
        </div>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded mt-2" disabled={loading}>
          {loading ? "Adding..." : "Add Dispute"}
        </button>
        <button type="button" className="ml-2 bg-gray-200 px-4 py-2 rounded" onClick={exportPDF}>
          Export PDF
        </button>
      </form>
      {/* Dispute List Table */}
      <div className="overflow-x-auto mb-8">
        <table className="min-w-full bg-white rounded shadow">
          <thead>
            <tr className="bg-gray-100">
              <th className="py-2 px-4 text-left">Client</th>
              <th className="py-2 px-4 text-left">Bureau</th>
              <th className="py-2 px-4 text-left">Type</th>
              <th className="py-2 px-4 text-left">Status</th>
              <th className="py-2 px-4 text-left">Date Created</th>
              <th className="py-2 px-4 text-left">Reason</th>
              <th className="py-2 px-4 text-left">Notes</th>
            </tr>
          </thead>
          <tbody>
            {disputes.length === 0 ? (
              <tr>
                <td colSpan={7} className="text-center py-4 text-gray-500">
                  No disputes found.
                </td>
              </tr>
            ) : (
              disputes.map((dispute) => (
                <tr key={dispute.id} className="border-b">
                  <td className="py-2 px-4">{dispute.clientName}</td>
                  <td className="py-2 px-4">{dispute.bureau}</td>
                  <td className="py-2 px-4">{dispute.type}</td>
                  <td className="py-2 px-4">{dispute.status}</td>
                  <td className="py-2 px-4">{dispute.dateCreated}</td>
                  <td className="py-2 px-4">{dispute.reason}</td>
                  <td className="py-2 px-4">{dispute.notes}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import jsPDF from 'jspdf';
import { db } from "@/lib/firebase";
import {
  collection,
  addDoc,
  updateDoc,
  doc,
  getDocs,
  query,
  where,
  Timestamp
} from 'firebase/firestore';

const TEMPLATES = [
  {
    type: 'initial',
    label: 'Initial Dispute Letter',
    body: `To Whom It May Concern,\n\nI am writing to dispute the following information on my credit report. Please investigate the items listed below and remove any inaccurate information.\n\nAccount Name: {{accountName}}\nAccount Number: {{accountNumber}}\nReason: {{reason}}\n\nSincerely,\n{{clientName}}\n{{clientAddress}}`
  },
  {
    type: 'followup',
    label: 'Follow-Up Letter',
    body: `To Whom It May Concern,\n\nThis is a follow-up to my previous dispute regarding the account below. Please provide an update on the investigation.\n\nAccount Name: {{accountName}}\nAccount Number: {{accountNumber}}\nDate of First Dispute: {{dateSent}}\n\nSincerely,\n{{clientName}}\n{{clientAddress}}`
  },
  {
    type: 'goodwill',
    label: 'Goodwill Letter',
    body: `To Whom It May Concern,\n\nI am requesting a goodwill adjustment for the following account, which has been paid in full. Please consider removing the negative mark as a gesture of goodwill.\n\nAccount Name: {{accountName}}\nAccount Number: {{accountNumber}}\n\nSincerely,\n{{clientName}}\n{{clientAddress}}`
  },
  {
    type: 'identity',
    label: 'Identity Theft Letter',
    body: `To Whom It May Concern,\n\nI am a victim of identity theft and am disputing the following fraudulent account. Please investigate and remove this item from my credit report.\n\nAccount Name: {{accountName}}\nAccount Number: {{accountNumber}}\n\nSincerely,\n{{clientName}}\n{{clientAddress}}`
  },
  {
    type: 'verification',
    label: 'Verification Request Letter',
    body: `To Whom It May Concern,\n\nI am requesting verification of the following account. Please provide documentation supporting its accuracy.\n\nAccount Name: {{accountName}}\nAccount Number: {{accountNumber}}\n\nSincerely,\n{{clientName}}\n{{clientAddress}}`
  },
];

function mailMerge(template, data) {
  let result = template;
  Object.keys(data).forEach(key => {
    result = result.replace(new RegExp(`{{${key}}}`, 'g'), data[key] || '');
  });
  return result;
}

export default function DisputeLetterGenerator({ client = {} }) {
  const [selectedTemplate, setSelectedTemplate] = useState(TEMPLATES[0].type);
  const [fields, setFields] = useState({
    clientName: client.name || '',
    clientAddress: client.address || '',
    accountName: '',
    accountNumber: '',
    reason: '',
    dateSent: '',
  });
  const [status, setStatus] = useState('draft');
  const [notes, setNotes] = useState('');
  const [saving, setSaving] = useState(false);
  const [letters, setLetters] = useState([]);

  // Load existing letters for this client
  useEffect(() => {
    async function fetchLetters() {
      if (!client.id) return;
      const q = query(collection(db, 'disputeLetters'), where('clientId', '==', client.id));
      const snapshot = await getDocs(q);
      setLetters(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    }
    fetchLetters();
  }, [client.id]);

  const templateObj = TEMPLATES.find(t => t.type === selectedTemplate);
  const letterPreview = mailMerge(templateObj.body, fields);

  const handleFieldChange = e => {
    const { name, value } = e.target;
    setFields(prev => ({ ...prev, [name]: value }));
  };

  const handleTemplateChange = e => {
    setSelectedTemplate(e.target.value);
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      const docRef = await addDoc(collection(db, 'disputeLetters'), {
        clientId: client.id || '',
        templateType: selectedTemplate,
        dateSent: fields.dateSent ? Timestamp.fromDate(new Date(fields.dateSent)) : null,
        status,
        clientInfo: {
          name: fields.clientName,
          address: fields.clientAddress,
          ssn: client.ssn || '',
        },
        accountDetails: {
          creditor: fields.accountName,
          accountNumber: fields.accountNumber,
          disputeReason: fields.reason,
          amount: client.amount || 0,
        },
        responseDeadline: fields.dateSent ? Timestamp.fromDate(new Date(new Date(fields.dateSent).getTime() + 30*24*60*60*1000)) : null,
        notes,
      });
      setSaving(false);
      alert('Letter saved to database!');
    } catch (err) {
      setSaving(false);
      alert('Error saving letter: ' + err.message);
    }
  };

  const exportPDF = () => {
    const doc = new jsPDF();
    doc.setFont('times', 'normal');
    doc.setFontSize(12);
    doc.text('Speedy Credit Repair', 10, 10);
    doc.text(letterPreview, 10, 30);
    doc.save('dispute_letter.pdf');
  };

  return (
    <section className="bg-white rounded-2xl shadow-2xl p-8 mb-8 w-full max-w-2xl mx-auto border border-gray-200">
      <h2 className="text-2xl font-bold mb-4">Dispute Letter Generator</h2>
      <div className="mb-4">
        <label className="block font-semibold mb-1">Select Template</label>
        <select value={selectedTemplate} onChange={handleTemplateChange} className="w-full border rounded px-2 py-1">
          {TEMPLATES.map(t => (
            <option key={t.type} value={t.type}>{t.label}</option>
          ))}
        </select>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
        <div>
          <label className="block font-semibold mb-1">Client Name</label>
          <input type="text" name="clientName" value={fields.clientName} onChange={handleFieldChange} className="w-full border rounded px-2 py-1" />
        </div>
        <div>
          <label className="block font-semibold mb-1">Client Address</label>
          <input type="text" name="clientAddress" value={fields.clientAddress} onChange={handleFieldChange} className="w-full border rounded px-2 py-1" />
        </div>
        <div>
          <label className="block font-semibold mb-1">Account Name</label>
          <input type="text" name="accountName" value={fields.accountName} onChange={handleFieldChange} className="w-full border rounded px-2 py-1" />
        </div>
        <div>
          <label className="block font-semibold mb-1">Account Number</label>
          <input type="text" name="accountNumber" value={fields.accountNumber} onChange={handleFieldChange} className="w-full border rounded px-2 py-1" />
        </div>
        {selectedTemplate === 'initial' && (
          <div className="md:col-span-2">
            <label className="block font-semibold mb-1">Reason for Dispute</label>
            <input type="text" name="reason" value={fields.reason} onChange={handleFieldChange} className="w-full border rounded px-2 py-1" />
          </div>
        )}
        {selectedTemplate === 'followup' && (
          <div className="md:col-span-2">
            <label className="block font-semibold mb-1">Date of First Dispute</label>
            <input type="date" name="dateSent" value={fields.dateSent} onChange={handleFieldChange} className="w-full border rounded px-2 py-1" />
          </div>
        )}
      </div>
      <div className="mb-4">
        <label className="block font-semibold mb-1">Letter Preview</label>
        <textarea value={letterPreview} readOnly className="w-full border rounded px-2 py-1" rows={10} />
      </div>
      <div className="mb-4">
        <label className="block font-semibold mb-1">Notes</label>
        <textarea value={notes} onChange={e => setNotes(e.target.value)} className="w-full border rounded px-2 py-1" rows={2} />
      </div>
      <div className="flex gap-4">
        <button onClick={exportPDF} className="px-4 py-2 bg-green-600 text-white rounded font-bold shadow">Export as PDF</button>
        <button onClick={handleSave} disabled={saving} className="px-4 py-2 bg-blue-600 text-white rounded font-bold shadow">{saving ? 'Saving...' : 'Save to Database'}</button>
      </div>
      {letters.length > 0 && (
        <div className="mt-8">
          <h3 className="font-bold mb-2">Saved Letters</h3>
          <ul className="list-disc ml-6">
            {letters.map(l => (
              <li key={l.id} className="mb-2">
                <span className="font-semibold">{l.templateType}</span> - Status: <span className="font-bold">{l.status}</span> - Sent: {l.dateSent?.toDate ? l.dateSent.toDate().toLocaleDateString() : ''}
                <br />Account: {l.accountDetails?.creditor} #{l.accountDetails?.accountNumber}
                <br />Deadline: {l.responseDeadline?.toDate ? l.responseDeadline.toDate().toLocaleDateString() : ''}
                <br />Notes: {l.notes}
              </li>
            ))}
          </ul>
        </div>
      )}
    </section>
  );
}

import React, { useState, useEffect } from 'react';
import { db } from '@/lib/firebase';
import { collection, query, where, orderBy, onSnapshot, addDoc, updateDoc, doc } from 'firebase/firestore';
import { useAuth } from '@/contexts/AuthContext';
import { DocumentTextIcon, ClockIcon, CheckCircleIcon, XCircleIcon } from '@heroicons/react/24/outline';

const DisputeTracker = () => {
  const { user } = useAuth();
  const [disputes, setDisputes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    const q = query(
      collection(db, 'disputes'),
      where('clientEmail', '==', user.email),
      orderBy('createdAt', 'desc')
    );

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const disputeData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setDisputes(disputeData);
      setLoading(false);
    });

    return () => unsubscribe();
  }, [user]);

  const statusConfig = {
    pending: { color: 'yellow', icon: ClockIcon, label: 'Pending' },
    sent: { color: 'blue', icon: DocumentTextIcon, label: 'Sent to Bureau' },
    investigating: { color: 'purple', icon: ClockIcon, label: 'Under Investigation' },
    resolved: { color: 'green', icon: CheckCircleIcon, label: 'Resolved' },
    rejected: { color: 'red', icon: XCircleIcon, label: 'Rejected' }
  };

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <h2 className="text-2xl font-bold mb-4">Dispute Tracker</h2>
      {loading ? (
        <div className="flex justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        </div>
      ) : disputes.length === 0 ? (
        <div className="text-center py-8 text-gray-500">
          <p>No disputes found.</p>
        </div>
      ) : (
        <div className="grid gap-4">
          {disputes.map(dispute => {
            const status = statusConfig[dispute.status] || statusConfig['pending'];
            const StatusIcon = status.icon;
            return (
              <div key={dispute.id} className={`flex items-center justify-between p-4 border rounded-lg dark:border-gray-700 bg-white dark:bg-gray-800`}>
                <div className="flex items-center gap-3">
                  <StatusIcon className={`h-8 w-8 text-${status.color}-600`} />
                  <div>
                    <p className="font-medium text-gray-900 dark:text-white">{dispute.subject || 'Dispute'}</p>
                    <p className="text-xs text-gray-500">{dispute.bureau || 'Unknown Bureau'}</p>
                    <p className="text-xs text-gray-500">{new Date(dispute.createdAt?.toDate ? dispute.createdAt.toDate() : dispute.createdAt).toLocaleDateString()}</p>
                  </div>
                </div>
                <span className={`text-xs px-2 py-1 rounded-full bg-${status.color}-100 text-${status.color}-800`}>{status.label}</span>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default DisputeTracker;

import React from 'react';
import { 
  DocumentIcon, 
  ChatBubbleLeftIcon, 
  UserGroupIcon,
  ChartBarIcon,
  FolderOpenIcon 
} from '@heroicons/react/24/outline';

export const EmptyDocuments = () => (
