import React, { useState , useEffect } from "react";

import { db } from "@/lib/firebase";
import { collection, query, onSnapshot } from 'firebase/firestore';

const demoExports = [
  { id: 1, type: "Client List", date: "2025-08-10", status: "Completed" },
  { id: 2, type: "Dispute History", date: "2025-08-12", status: "Completed" },
];

export default function Export() {
  const [exports, setExports] = useState(demoExports);
  const [useDemo, setUseDemo] = useState(true);

  useEffect(() => {
    let unsubscribe = null;
    try {
      const q = query(collection(db, 'export'));
      unsubscribe = onSnapshot(q,
        (snapshot) => {
          const firebaseData = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
          setExports(firebaseData);
          setUseDemo(false);
        },
        (error) => {
          setExports(demoExports);
          setUseDemo(true);
        }
      );
    } catch (error) {
      setExports(demoExports);
      setUseDemo(true);
    }
    return () => unsubscribe && unsubscribe();
  }, []);

  return (
    <div className="min-h-screen bg-white text-gray-900">
      <main className="mx-auto max-w-4xl px-6 py-10">
        <h1 className="text-3xl font-bold mb-4">Export</h1>
        <div className="overflow-x-auto mb-8">
          <table className="min-w-full bg-white rounded shadow">
            <thead>
              <tr className="bg-gray-100">
                <th className="py-2 px-4 text-left">Type</th>
                <th className="py-2 px-4 text-left">Date</th>
                <th className="py-2 px-4 text-left">Status</th>
              </tr>
            </thead>
            <tbody>
              {exports.length === 0 ? (
                <tr>
                  <td colSpan={3} className="text-center py-4 text-gray-500">
                    No exports found.
                  </td>
                </tr>
              ) : (
                exports.map((exp) => (
                  <tr key={exp.id} className="border-b">
                    <td className="py-2 px-4">{exp.type}</td>
                    <td className="py-2 px-4">{exp.date}</td>
                    <td className="py-2 px-4">{exp.status}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
}

import React from 'react';
import { usePermission } from '../usePermission';

const FeatureGuard = ({ 
  feature, 
  children, 
  fallback = null, 
  showMessage = true,
  debug = false
}) => {
  const { hasFeatureAccess, isAuthenticated, isMasterAdmin, loading } = usePermission();

  // Debug logging during development
  if (debug && process.env.NODE_ENV === 'development') {
    // ...removed for production...
    console.debug({
      isAuthenticated,
      isMasterAdmin,
      hasAccess: hasFeatureAccess(feature),
      loading
    });
  }

  // Show loading state while permissions are being checked
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[200px]">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        <span className="ml-3 text-gray-600">Loading...</span>
      </div>
    );
  }

  // Check authentication first
  if (!isAuthenticated) {
    if (showMessage) {
      return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50">
          <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-6 text-center">
            <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
              </svg>
            </div>
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Authentication Required</h3>
            <p className="text-gray-600 mb-4">Please sign in to access this feature.</p>
            <a 
              href="/login" 
              className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
            >
              Sign In
            </a>
          </div>
        </div>
      );
    }
    return fallback;
  }

  // Check feature access using the permission system
  if (!hasFeatureAccess(feature)) {
    if (showMessage) {
      return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50">
          <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-6 text-center">
            <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M18.364 18.364A9 9 0 005.636 5.636m12.728 12.728L5.636 5.636m12.728 12.728L18.364 5.636M5.636 18.364l12.728-12.728" />
              </svg>
            </div>
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Access Restricted</h3>
            <p className="text-gray-600 mb-4">
              You don't have permission to access "{feature}". Please contact your administrator if you believe this is an error.
            </p>
            <a 
              href="/" 
              className="bg-gray-600 text-white px-6 py-2 rounded-lg hover:bg-gray-700 transition-colors"
            >
              Go to Dashboard
            </a>
            {debug && process.env.NODE_ENV === 'development' && (
              <details className="mt-4 text-left">
                <summary className="text-sm text-gray-500 cursor-pointer">Debug Info</summary>
                <pre className="mt-2 text-xs bg-gray-100 p-2 rounded overflow-auto">
                  {JSON.stringify({
                    feature,
                    isAuthenticated,
                    isMasterAdmin,
                    hasAccess: hasFeatureAccess(feature)
                  }, null, 2)}
                </pre>
              </details>
            )}
          </div>
        </div>
      );
    }
    return fallback;
  }

  // Access granted - render the protected content
  return children;
};

export default FeatureGuard;

