import React, { useEffect, useState } from 'react';
import EditClientForm from '@/components/IDIQ/EditClientForm';
import CreateDisputeForm from '@/components/disputes/CreateDisputeForm';
import AddScoreUpdate from '@/components/Scores/AddScoreUpdate';
import { collection, getDocs, query, orderBy } from 'firebase/firestore';
import { db } from '@/lib/firebase';

const ClientDetailView = ({ client, onClose, onEdit, onDelete, onRefresh }) => {
  // All hooks at the top
  const [showConfirm, setShowConfirm] = useState(false);
  const [editing, setEditing] = useState(false);
  const [showDisputeForm, setShowDisputeForm] = useState(false);
  const [disputes, setDisputes] = useState([]);
  const [loadingDisputes, setLoadingDisputes] = useState(true);
  const [showScoreForm, setShowScoreForm] = useState(false);
  const [scoreHistory, setScoreHistory] = useState([]);
  const [loadingScores, setLoadingScores] = useState(true);

  // Fetch score history for client
  useEffect(() => {
    if (!client?.id) return;
    const fetchScores = async () => {
      setLoadingScores(true);
      try {
        const q = query(collection(db, 'clients', client.id, 'scoreHistory'), orderBy('date', 'asc'));
        const snap = await getDocs(q);
        const scoreList = [];
        snap.forEach(docSnap => {
          scoreList.push({ id: docSnap.id, ...docSnap.data() });
        });
        setScoreHistory(scoreList);
      } catch (err) {
        setScoreHistory([]);
      } finally {
        setLoadingScores(false);
      }
    };
    fetchScores();
  }, [client, showScoreForm]);

  // Fetch disputes for client
  useEffect(() => {
    if (!client?.id) return;
    const fetchDisputes = async () => {
      setLoadingDisputes(true);
      try {
        const q = query(collection(db, 'clients', client.id, 'disputes'), orderBy('createdAt', 'desc'));
        const snap = await getDocs(q);
        const disputeList = [];
        snap.forEach(docSnap => {
          disputeList.push({ id: docSnap.id, ...docSnap.data() });
        });
        setDisputes(disputeList);
      } catch (err) {
        setDisputes([]);
      } finally {
        setLoadingDisputes(false);
      }
    };
    fetchDisputes();
  }, [client, showDisputeForm]);

  const handleAddScore = () => {
    setShowScoreForm(true);
  };
  const handleCloseScoreForm = () => {
    setShowScoreForm(false);
  };
  const handleScoreAdded = () => {
    setShowScoreForm(false);
    // Will auto-refresh score history via useEffect
  };
  const handleCreateDispute = () => {
    setShowDisputeForm(true);
  };
  const handleCloseDisputeForm = () => {
    setShowDisputeForm(false);
  };
  const handleDisputeCreated = () => {
    setShowDisputeForm(false);
    // Will auto-refresh disputes via useEffect
  };

  const handleEditClick = () => {
    setEditing(true);
  };
  const handleEditClose = () => {
    setEditing(false);
  };
  const handleEditSuccess = () => {
    setEditing(false);
    if (onRefresh) onRefresh();
  };

  if (!client) return null;

  return (
    <div style={{position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.25)', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
      <div style={{background: '#fff', padding: 32, borderRadius: 10, minWidth: 340, boxShadow: '0 2px 16px #aaa', position: 'relative'}}>
        <h2 style={{fontSize: 22, fontWeight: 'bold', marginBottom: 18}}>Client Details</h2>
        {editing ? (
          <EditClientForm client={client} onClose={handleEditClose} onSuccess={handleEditSuccess} />
        ) : (
          <>
            <div style={{marginBottom: 16}}>
              <div><strong>Name:</strong> {client.firstName} {client.lastName}</div>
              <div><strong>Email:</strong> {client.email}</div>
              <div><strong>Phone:</strong> {client.phone}</div>
              <div><strong>SSN (last 4):</strong> {client.ssnLast4}</div>
              <div><strong>Score:</strong> {client.score}</div>
              <div style={{marginTop: 10, display: 'flex', gap: 12}}>
                <button onClick={handleCreateDispute} style={{padding: '8px 18px', fontSize: 16, background: '#ffa726', color: '#fff', border: 'none', borderRadius: 6, cursor: 'pointer', fontWeight: 'bold'}}>Create Dispute</button>
                <button onClick={handleAddScore} style={{padding: '8px 18px', fontSize: 16, background: '#388e3c', color: '#fff', border: 'none', borderRadius: 6, cursor: 'pointer', fontWeight: 'bold'}}>Update Scores</button>
              </div>
            </div>
            {showScoreForm && (
              <AddScoreUpdate clientId={client.id} onClose={handleCloseScoreForm} onSuccess={handleScoreAdded} />
            )}
            <div style={{marginTop: 18}}>
              <h3 style={{fontWeight: 'bold', fontSize: 17}}>Score History ({scoreHistory.length})</h3>
              {loadingScores ? (
                <div style={{color: '#888'}}>Loading score history...</div>
              ) : scoreHistory.length === 0 ? (
                <div style={{color: '#888'}}>No score history found.</div>
              ) : (
                <ul style={{marginTop: 8}}>
                  {scoreHistory.map(s => (
                    <li key={s.id} style={{marginBottom: 8, background: '#e8f5e9', padding: 10, borderRadius: 6}}>
                      <div><strong>Date:</strong> {s.date}</div>
                      <div><strong>Experian:</strong> {s.experian}</div>
                      <div><strong>Equifax:</strong> {s.equifax}</div>
                      <div><strong>TransUnion:</strong> {s.transunion}</div>
                      <div><strong>Avg Score:</strong> {s.avgScore}</div>
                      <div><strong>Notes:</strong> {s.notes}</div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
            {showDisputeForm && (
              <CreateDisputeForm clientId={client.id} onClose={handleCloseDisputeForm} onSuccess={handleDisputeCreated} />
            )}
            <div style={{marginTop: 18}}>
              <h3 style={{fontWeight: 'bold', fontSize: 17}}>Disputes ({disputes.length})</h3>
              {loadingDisputes ? (
                <div style={{color: '#888'}}>Loading disputes...</div>
              ) : disputes.length === 0 ? (
                <div style={{color: '#888'}}>No disputes found.</div>
              ) : (
                <ul style={{marginTop: 8}}>
                  {disputes.map(d => (
                    <li key={d.id} style={{marginBottom: 8, background: '#f6f6ff', padding: 10, borderRadius: 6}}>
                      <div><strong>Creditor:</strong> {d.creditor}</div>
                      <div><strong>Account #:</strong> {d.accountNumber}</div>
                      <div><strong>Type:</strong> {d.type}</div>
                      <div><strong>Status:</strong> {d.status}</div>
                      <div><strong>Date:</strong> {d.dateSubmitted ? new Date(d.dateSubmitted).toLocaleDateString() : ''}</div>
                      <div><strong>Reason:</strong> {d.reason}</div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
            <div style={{display: 'flex', gap: 12, marginTop: 10}}>
              <button onClick={handleEditClick} style={{padding: '8px 18px', fontSize: 16, background: '#1976d2', color: '#fff', border: 'none', borderRadius: 6, cursor: 'pointer', fontWeight: 'bold'}}>Edit</button>
              <button onClick={() => setShowConfirm(true)} style={{padding: '8px 18px', fontSize: 16, background: '#d32f2f', color: '#fff', border: 'none', borderRadius: 6, cursor: 'pointer', fontWeight: 'bold'}}>Delete</button>
              <button onClick={onClose} style={{padding: '8px 18px', fontSize: 16, background: '#aaa', color: '#fff', border: 'none', borderRadius: 6, cursor: 'pointer', fontWeight: 'bold'}}>Close</button>
            </div>
            {showConfirm && (
              <div style={{marginTop: 18, background: '#fff7f7', padding: 12, borderRadius: 6, boxShadow: '0 1px 4px #eee'}}>
                <div style={{marginBottom: 10}}>Are you sure you want to delete this client?</div>
                <button onClick={() => { setShowConfirm(false); onDelete(); }} style={{padding: '6px 16px', background: '#d32f2f', color: '#fff', border: 'none', borderRadius: 4, fontWeight: 'bold', marginRight: 8}}>Yes, Delete</button>
                <button onClick={() => setShowConfirm(false)} style={{padding: '6px 16px', background: '#aaa', color: '#fff', border: 'none', borderRadius: 4, fontWeight: 'bold'}}>Cancel</button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default ClientDetailView;

import React, { useState, useEffect } from 'react';
import CreditAnalytics from '../IDIQIntegration/CreditAnalytics';
import ClientCreditCard from '../IDIQIntegration/ClientCreditCard';
import AddClientForm from './AddClientForm';
import ClientDetailView from './ClientDetailView';
import { collection, getDocs, query, orderBy, deleteDoc, doc } from 'firebase/firestore';
import { db } from "../lib/firebase";

// --- Real Metrics ---
// These must be inside the component, after clients is defined

const IDIQDashboard = () => {
  const [mode, setMode] = useState(() => localStorage.getItem('idiqMode') || 'mock');
  const [clients, setClients] = useState([]); // <-- Initialize clients state
  const [loadingClients, setLoadingClients] = useState(true);
  const [selectedClient, setSelectedClient] = useState(null);
  const [disputePipeline, setDisputePipeline] = useState(initialDisputePipeline);
  const [scoreDistribution, setScoreDistribution] = useState(initialScoreDistribution);
  const [showAddClient, setShowAddClient] = useState(false);

  useEffect(() => {
    localStorage.setItem('idiqMode', mode);
  }, [mode]);

  // Fetch clients from Firestore
  useEffect(() => {
    const fetchClients = async () => {
      setLoadingClients(true);
      try {
        const q = query(collection(db, 'clients'), orderBy('createdAt', 'desc'));
        const snap = await getDocs(q);
        const clientList = [];
        snap.forEach(docSnap => {
          clientList.push({ id: docSnap.id, ...docSnap.data() });
        });
        setClients(clientList);
      } catch (err) {
        console.error('Error fetching clients:', err);
      } finally {
        setLoadingClients(false);
      }
    };
    fetchClients();
  }, [showAddClient]);

  const isLive = mode === 'live';

  // --- Interactivity Handlers ---
  const handleClientClick = (client) => {
    setSelectedClient(client);
  };
  const handleCloseDetail = () => {
    setSelectedClient(null);
  };
  const handleEditClient = () => {
    alert('Edit feature coming soon');
  };
  const handleDeleteClient = async () => {
    if (!selectedClient) return;
    try {
      await deleteDoc(doc(db, 'clients', selectedClient.id));
      setSelectedClient(null);
      // Refresh client list
      const q = query(collection(db, 'clients'), orderBy('createdAt', 'desc'));
      const snap = await getDocs(q);
      const clientList = [];
      snap.forEach(docSnap => {
        clientList.push({ id: docSnap.id, ...docSnap.data() });
      });
      setClients(clientList);
    } catch (err) {
      alert('Failed to delete client');
      console.error(err);
    }
  };
  const handleRefresh = () => {
    // Slightly change mock numbers for demo
    setClients(clients.map(c => ({ ...c, score: c.score + (Math.random() > 0.5 ? 1 : -1) * Math.floor(Math.random() * 10) })));
    setDisputePipeline(disputePipeline.map(d => ({ ...d, count: d.count + (Math.random() > 0.5 ? 1 : -1) * Math.floor(Math.random() * 2) })));
    setScoreDistribution(scoreDistribution.map(s => ({ ...s, count: Math.max(0, s.count + (Math.random() > 0.5 ? 1 : -1) * Math.floor(Math.random() * 2)) })));
  };
  const handleViewDetails = (status) => {
    console.log('View Details for status:', status);
  };
  const handleScoreBarClick = (range, count) => {
    alert(`${count} clients in ${range} range`);
  };
  const handleAddClient = () => {
    setShowAddClient(true);
  };
  const handleCloseAddClient = () => {
    setShowAddClient(false);
  };
  const handleClientAdded = async () => {
    // Simulate refresh: In real app, fetch from Firestore
    setShowAddClient(false);
    // Optionally, you could fetch updated clients from Firestore here
    // For demo, just refresh mock data
    setClients([...clients]);
  };

  // --- Styles ---
  const clientStyle = {
    cursor: 'pointer',
    color: '#176c2f',
    textDecoration: 'underline',
    fontWeight: 'bold',
    transition: 'color 0.2s',
  };

  return (
    <div style={{padding: 32}}>
      <h1 style={{fontSize: 28, fontWeight: 'bold'}}>IDIQ Integration Dashboard</h1>
      <button onClick={handleAddClient} style={{marginBottom: 16, padding: '8px 18px', fontSize: 16, background: '#1976d2', color: '#fff', border: 'none', borderRadius: 6, cursor: 'pointer', fontWeight: 'bold'}}>
        Add New Client
      </button>
      {showAddClient && (
        <AddClientForm onClose={handleCloseAddClient} onSuccess={handleClientAdded} />
      )}
      {selectedClient && (
        <ClientDetailView
          client={selectedClient}
          onClose={handleCloseDetail}
          onEdit={handleEditClient}
          onDelete={handleDeleteClient}
          onRefresh={async () => {
            // Refresh client list and detail view after edit
            const q = query(collection(db, 'clients'), orderBy('createdAt', 'desc'));
            const snap = await getDocs(q);
            const clientList = [];
            snap.forEach(docSnap => {
              clientList.push({ id: docSnap.id, ...docSnap.data() });
            });
            setClients(clientList);
            // Update selectedClient with latest data
            const updated = clientList.find(c => c.id === selectedClient.id);
            if (updated) setSelectedClient(updated);
          }}
        />
      )}
      <div style={{marginBottom: 16}}>
        <label style={{fontWeight: 'bold', marginRight: 12}}>Mode:</label>
        <select
          value={mode}
          onChange={e => setMode(e.target.value)}
          style={{padding: '4px 12px', fontSize: 16}}
        >
          <option value="mock">Mock Mode</option>
          <option value="live" disabled>Live Mode (IAM not configured)</option>
        </select>
      </div>
      {isLive ? (
        <div style={{background: '#d4f7d4', color: '#176c2f', padding: 12, borderRadius: 6, marginBottom: 16, fontWeight: 'bold'}}>
          Connected to IDIQ API (Live Mode)
        </div>
      ) : (
        <div style={{background: '#fff7d4', color: '#a67c00', padding: 12, borderRadius: 6, marginBottom: 16, fontWeight: 'bold'}}>
          Using Mock Data (Mock Mode)
        </div>
      )}

      {/* --- Analytics Section --- */}
      <div style={{marginBottom: 24, background: '#e3f2fd', borderRadius: 8, padding: 20, boxShadow: '0 2px 8px #eee'}}>
        <h2 style={{fontSize: 20, fontWeight: 'bold', marginBottom: 12}}>Analytics</h2>
        <div style={{display: 'flex', gap: 32}}>
          <div><strong>Total Clients:</strong> {totalClients}</div>
          <div><strong>Enrolled:</strong> {enrolledClients}</div>
          <div><strong>Active Disputes:</strong> {activeDisputes}</div>
          <div><strong>Resolved:</strong> {resolvedDisputes}</div>
          <div><strong>Avg Score Gain:</strong> {avgScoreGain}</div>
        </div>
      </div>

      {/* --- Recent Activity Section --- */}
      <div style={{marginBottom: 24, background: '#f9f9f9', borderRadius: 8, padding: 20, boxShadow: '0 2px 8px #eee'}}>
        <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
          <h2 style={{fontSize: 22, fontWeight: 'bold'}}>Recent Activity</h2>
          <button onClick={handleRefresh} style={{padding: '6px 16px', fontSize: 15, background: '#ffa726', color: '#fff', border: 'none', borderRadius: 6, cursor: 'pointer', fontWeight: 'bold'}}>Refresh Data</button>
        </div>
        {loadingClients ? (
          <div style={{marginTop: 16, color: '#888'}}>Loading clients...</div>
        ) : (
          <ul style={{marginTop: 12}}>
            {recentActivity.map((act, idx) => (
              <li key={idx} style={{marginBottom: 8}}>
                <span
                  style={clientStyle}
                  onClick={() => handleClientClick(act.client)}
                  onMouseOver={e => (e.target.style.color = '#1976d2')}
                  onMouseOut={e => (e.target.style.color = '#176c2f')}
                >
                  {act.name}
                </span>
                {` - ${act.action} (${act.ago})`}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div style={{marginBottom: 24, background: '#f6f6ff', borderRadius: 8, padding: 20, boxShadow: '0 2px 8px #eee'}}>
        <h2 style={{fontSize: 20, fontWeight: 'bold'}}>Dispute Pipeline</h2>
        <div style={{display: 'flex', gap: 24, marginTop: 12}}>
          {disputePipeline.map(dp => (
            <div key={dp.status} style={{background: '#fff', borderRadius: 6, padding: 16, boxShadow: '0 1px 4px #ddd', minWidth: 120, textAlign: 'center'}}>
              <div style={{fontSize: 18, fontWeight: 'bold'}}>{dp.count}</div>
              <div style={{fontSize: 15, color: '#555'}}>{dp.status}</div>
              <button onClick={() => handleViewDetails(dp.status)} style={{marginTop: 8, padding: '4px 10px', fontSize: 13, background: '#1976d2', color: '#fff', border: 'none', borderRadius: 4, cursor: 'pointer'}}>View Details</button>
            </div>
          ))}
        </div>
      </div>

      <div style={{marginBottom: 24, background: '#e8f5e9', borderRadius: 8, padding: 20, boxShadow: '0 2px 8px #eee'}}>
        <h2 style={{fontSize: 20, fontWeight: 'bold'}}>Score Distribution</h2>
        <div style={{display: 'flex', gap: 18, marginTop: 12}}>
          {scoreDistribution.map(sd => (
            <div
              key={sd.range}
              style={{background: '#fff', borderRadius: 6, padding: 16, boxShadow: '0 1px 4px #ddd', minWidth: 100, textAlign: 'center', cursor: 'pointer'}}
              onClick={() => handleScoreBarClick(sd.range, sd.count)}
            >
              <div style={{fontSize: 17, fontWeight: 'bold'}}>{sd.count} ({sd.percent}%)</div>
              <div style={{fontSize: 14, color: '#388e3c'}}>{sd.range}</div>
            </div>
          ))}
        </div>
      </div>

      {/* Conditional data fetching based on mode */}
      <CreditAnalytics mode={mode} />
      <ClientCreditCard mode={mode} />
    </div>
  );
};

