import { useEffect, useState, useCallback } from "react";
import { collection, query, where, orderBy, onSnapshot, addDoc, getDocs } from "firebase/firestore";
import { db } from "../lib/firebase";

export function useFirestore(collectionName, options = {}) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchData = useCallback(() => {
    setLoading(true);
    setError(null);
    try {
      let ref = collection(db, collectionName);
      let q = ref;
      if (options.whereConditions) {
        options.whereConditions.forEach(cond => {
          q = query(q, where(cond.field, cond.operator, cond.value));
        });
      }
      if (options.orderByField) {
        q = query(q, orderBy(options.orderByField, options.orderDirection || "asc"));
      }
      if (options.realtime) {
        return onSnapshot(q, (snapshot) => {
          setData(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
          setLoading(false);
        }, (err) => {
          setError(err.message);
          setLoading(false);
        });
      } else {
        getDocs(q).then(snapshot => {
          setData(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
          setLoading(false);
        }).catch(err => {
          setError(err.message);
          setLoading(false);
        });
      }
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  }, [collectionName, JSON.stringify(options)]);

  useEffect(() => {
    let unsub;
    if (options.realtime) {
      unsub = fetchData();
      return () => unsub && unsub();
    } else {
      fetchData();
    }
  }, [fetchData, options.realtime]);

  const add = async (doc) => {
    try {
      await addDoc(collection(db, collectionName), {
        ...doc,
        createdAt: new Date()
      });
      if (!options.realtime) fetchData();
    } catch (err) {
      setError(err.message);
    }
  };

  return { data, loading, error, add };
}

import { useEffect, useState } from 'react';
import { db } from "@/lib/firebase";
import { collection, query, where, orderBy, onSnapshot } from 'firebase/firestore';

export default function useRealtimeLeads() {
  const [hotLeads, setHotLeads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    setLoading(true);
    setError('');
    const callsRef = collection(db, 'aiReceptionistCalls');
    const q = query(callsRef, where('leadScore', '>=', 8), orderBy('processedAt', 'desc'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const leads = [];
      snapshot.forEach(doc => {
        leads.push({ id: doc.id, ...doc.data() });
      });
      setHotLeads(leads);
      setLoading(false);
    }, (err) => {
      setError('Error loading hot leads');
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  return { hotLeads, loading, error };
}

import { useState, useEffect } from 'react';
import { useAuth } from '@/authContext';
import { collection, getDocs, doc, updateDoc, deleteDoc } from 'firebase/firestore';
import { db } from "@/lib/firebase";

export const useUserManagement = () => {
  const { isMasterAdmin, user } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch all users (admin only)
  const fetchUsers = async () => {
    if (!isMasterAdmin()) {
      setError('Access denied: Admin privileges required');
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const usersRef = collection(db, 'users');
      const snapshot = await getDocs(usersRef);
      const usersList = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setUsers(usersList);
    } catch (err) {
      setError('Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  // Update user role/permissions
  const updateUser = async (userId, updates) => {
    if (!isMasterAdmin()) throw new Error('Access denied');
    try {
      const userRef = doc(db, 'users', userId);
      await updateDoc(userRef, { ...updates, updatedAt: new Date().toISOString(), updatedBy: user?.uid });
      await fetchUsers();
      return true;
    } catch (err) {
      throw err;
    }
  };

  // Delete user
  const deleteUser = async (userId) => {
    if (!isMasterAdmin()) throw new Error('Access denied');
    try {
      await deleteDoc(doc(db, 'users', userId));
      await fetchUsers();
      return true;
    } catch (err) {
      throw err;
    }
  };

  // Disable/Enable user
  const toggleUserStatus = async (userId, status) => {
    return updateUser(userId, { status, role: status === 'disabled' ? 'disabled' : 'user' });
  };

  useEffect(() => {
    if (isMasterAdmin()) fetchUsers();
  }, [isMasterAdmin]);

  return { users, loading, error, fetchUsers, updateUser, deleteUser, toggleUserStatus };
};

