import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  HomeIcon, 
  ChartBarIcon, 
  DocumentTextIcon,
  FolderIcon,
  ChatBubbleLeftRightIcon,
  Bars3Icon,
  XMarkIcon,
  ArrowRightOnRectangleIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../../contexts/AuthContext';

const ClientLayout = ({ children }) => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const location = useLocation();
  const { user, logout } = useAuth();
  
  const navigation = [
    { name: 'Dashboard', href: '/client-portal', icon: HomeIcon },
    { name: 'My Credit Scores', href: '/client-portal/scores', icon: ChartBarIcon },
    { name: 'Dispute Status', href: '/client-portal/disputes', icon: DocumentTextIcon },
    { name: 'Documents', href: '/client-portal/documents', icon: FolderIcon },
    { name: 'Messages', href: '/client-portal/messages', icon: ChatBubbleLeftRightIcon },
  ];
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-green-50 dark:from-gray-900 dark:to-gray-800">
      {/* Mobile Header */}
      <div className="lg:hidden">
        <div className="fixed top-0 left-0 right-0 z-50 bg-white/90 dark:bg-gray-900/90 backdrop-blur-md border-b border-gray-200 dark:border-gray-700">
          <div className="flex items-center justify-between px-4 py-3">
            <h1 className="text-xl font-bold text-gray-900 dark:text-white">
              SpeedyCRM
            </h1>
            <button
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800"
            >
              {mobileMenuOpen ? (
                <XMarkIcon className="h-6 w-6" />
              ) : (
                <Bars3Icon className="h-6 w-6" />
              )}
            </button>
          </div>
        </div>
        
        {/* Mobile Menu Overlay */}
        {mobileMenuOpen && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="fixed inset-0 z-40 bg-white dark:bg-gray-900 pt-16"
          >
            <nav className="px-4 py-6 space-y-2">
              {navigation.map((item) => {
                const Icon = item.icon;
                const isActive = location.pathname === item.href;
                return (
                  <Link
                    key={item.name}
                    to={item.href}
                    onClick={() => setMobileMenuOpen(false)}
                    className={`flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                      isActive
                        ? 'bg-blue-100 text-blue-600 dark:bg-blue-900/50 dark:text-blue-400'
                        : 'hover:bg-gray-100 dark:hover:bg-gray-800'
                    }`}
                  >
                    <Icon className="h-5 w-5" />
                    <span className="font-medium">{item.name}</span>
                  </Link>
                );
              })}
              <button
                onClick={logout}
                className="flex items-center space-x-3 px-4 py-3 rounded-lg hover:bg-red-50 dark:hover:bg-red-900/20 text-red-600 dark:text-red-400 w-full"
              >
                <ArrowRightOnRectangleIcon className="h-5 w-5" />
                <span className="font-medium">Sign Out</span>
              </button>
            </nav>
          </motion.div>
        )}
      </div>
      
      {/* Desktop Sidebar */}
      <div className="hidden lg:fixed lg:inset-y-0 lg:flex lg:w-64 lg:flex-col">
        <div className="flex flex-col flex-1 bg-white/90 dark:bg-gray-900/90 backdrop-blur-md border-r border-gray-200 dark:border-gray-700">
          <div className="flex items-center h-16 px-6 border-b border-gray-200 dark:border-gray-700">
            <h1 className="text-xl font-bold text-gray-900 dark:text-white">
              SpeedyCRM Client
            </h1>
          </div>
          <nav className="flex-1 px-4 py-6 space-y-2">
            {navigation.map((item) => {
              const Icon = item.icon;
              const isActive = location.pathname === item.href;
              return (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                    isActive
                      ? 'bg-blue-100 text-blue-600 dark:bg-blue-900/50 dark:text-blue-400'
                      : 'hover:bg-gray-100 dark:hover:bg-gray-800'
                  }`}
                >
                  <Icon className="h-5 w-5" />
                  <span className="font-medium">{item.name}</span>
                </Link>
              );
            })}
          </nav>
          <div className="px-4 py-4 border-t border-gray-200 dark:border-gray-700">
            <button
              onClick={logout}
              className="flex items-center space-x-3 px-4 py-3 rounded-lg hover:bg-red-50 dark:hover:bg-red-900/20 text-red-600 dark:text-red-400 w-full"
            >
              <ArrowRightOnRectangleIcon className="h-5 w-5" />
              <span className="font-medium">Sign Out</span>
            </button>
          </div>
        </div>
      </div>
      
      {/* Main Content */}
      <div className="lg:pl-64">
        <main className="px-4 py-20 lg:py-8 lg:px-8">
          {children}
        </main>
      </div>
    </div>
  );
};

export default ClientLayout;

import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useAuth } from '../../contexts/AuthContext';
import { collection, query, where, orderBy, onSnapshot } from 'firebase/firestore';
import { db } from '@/lib/firebase';
import {
  ChartBarIcon,
  ArrowTrendingUpIcon,
  ArrowTrendingDownIcon,
  InformationCircleIcon,
  AcademicCapIcon,
  SparklesIcon,
  LockClosedIcon,
  BeakerIcon,
  DocumentChartBarIcon,
  ExclamationTriangleIcon
} from '@heroicons/react/24/outline';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const ClientScores = () => {
  const { user } = useAuth();
  const [scores, setScores] = useState([]);
  const [selectedView, setSelectedView] = useState('overview'); // overview, details, simulator
  const [selectedBureau, setSelectedBureau] = useState('all');
  const [loading, setLoading] = useState(true);
  const [simulatorEnabled, setSimulatorEnabled] = useState(false); // Admin controlled
  const [showEducation, setShowEducation] = useState(true);

  // Score model definitions
  const scoreModels = {
    'FICO8': { name: 'FICO¬Æ Score 8', range: '300-850', usage: 'Most common for credit cards & loans' },
    'VantageScore3': { name: 'VantageScore¬Æ 3.0', range: '300-850', usage: 'Used by many free credit monitoring services' },
    'FICOAuto': { name: 'FICO¬Æ Auto Score', range: '250-900', usage: 'Used for auto loan applications' },
    'FICOMortgage': { name: 'FICO¬Æ Mortgage Score', range: '300-850', usage: 'Used for home loan applications' },
    'FICOBankcard': { name: 'FICO¬Æ Bankcard Score', range: '250-900', usage: 'Used for credit card applications' }
  };

  useEffect(() => {
    if (!user) return;

    const scoresQuery = query(
      collection(db, 'creditScores'),
      where('clientEmail', '==', user.email),
      orderBy('date', 'desc')
    );

    const unsubscribe = onSnapshot(scoresQuery, (snapshot) => {
      const scoresData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
        date: doc.data().date?.toDate ? doc.data().date.toDate() : new Date(doc.data().date)
      }));
      setScores(scoresData);
      setLoading(false);
      checkSimulatorAccess();
    });

    return () => unsubscribe();
  }, [user]);

  const checkSimulatorAccess = async () => {
    setSimulatorEnabled(false);
  };

  // Get scores organized by model and bureau
  const getScoresByModel = () => {
    const organized = {};
    const bureaus = ['Equifax', 'Experian', 'TransUnion'];
    Object.keys(scoreModels).forEach(model => {
      organized[model] = {};
      bureaus.forEach(bureau => {
        const modelScores = scores.filter(s => 
          s.bureau === bureau && (s.model === model || (!s.model && model === 'FICO8'))
        );
        organized[model][bureau] = {
          current: modelScores[0]?.score || null,
          previous: modelScores[1]?.score || null,
          change: modelScores[0] && modelScores[1] 
            ? modelScores[0].score - modelScores[1].score 
            : 0,
          date: modelScores[0]?.date || null
        };
      });
    });
    return organized;
  };

  // Educational banner component
  const EducationalBanner = () => (
    <AnimatePresence>
      {showEducation && (
        <motion.div
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          exit={{ opacity: 0, height: 0 }}
          className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-900/20 dark:to-purple-900/20 rounded-xl p-6 mb-8 border border-blue-200 dark:border-blue-800"
        >
          <div className="flex items-start gap-4">
            <AcademicCapIcon className="h-8 w-8 text-blue-600 dark:text-blue-400 flex-shrink-0 mt-1" />
            <div className="flex-1">
              <h3 className="font-semibold text-gray-900 dark:text-white mb-2 text-lg">
                Did You Know? You Don&apos;t Have Just One Credit Score!
              </h3>
              <p className="text-gray-700 dark:text-gray-300 mb-3">
                You actually have dozens of different credit scores. Each lender may use a different scoring model 
                depending on what you&apos;re applying for. Your auto lender sees different scores than your credit card company!
              </p>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-3 mb-4">
                <div className="bg-white dark:bg-gray-800 rounded-lg p-3">
                  <span className="text-sm font-semibold text-gray-900 dark:text-white">FICO¬Æ Score 8</span>
                  <p className="text-xs text-gray-600 dark:text-gray-400 mt-1">Most common for general credit</p>
                </div>
                <div className="bg-white dark:bg-gray-800 rounded-lg p-3">
                  <span className="text-sm font-semibold text-gray-900 dark:text-white">Auto Scores</span>
                  <p className="text-xs text-gray-600 dark:text-gray-400 mt-1">Range 250-900 for car loans</p>
                </div>
                <div className="bg-white dark:bg-gray-800 rounded-lg p-3">
                  <span className="text-sm font-semibold text-gray-900 dark:text-white">Mortgage Scores</span>
                  <p className="text-xs text-gray-600 dark:text-gray-400 mt-1">Specific models for home loans</p>
                </div>
              </div>
              <button
                onClick={() => setShowEducation(false)}
                className="text-sm text-blue-600 hover:text-blue-700 dark:text-blue-400"
              >
                Got it, thanks!
              </button>
            </div>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );

  // ScoreView‚Ñ¢ Header
  const ScoreViewHeader = () => (
    <div className="mb-8">
      <div className="flex items-center gap-3 mb-2">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
          ScoreView‚Ñ¢
        </h1>
        <span className="px-3 py-1 bg-gradient-to-r from-blue-100 to-purple-100 dark:from-blue-900/30 dark:to-purple-900/30 text-blue-700 dark:text-blue-300 rounded-full text-sm font-semibold">
          Credit Intelligence
        </span>
      </div>
      <p className="text-gray-600 dark:text-gray-400">
        Your complete credit score analysis across all bureaus and scoring models
      </p>
    </div>
  );

  // View tabs
  const ViewTabs = () => (
    <div className="flex gap-2 mb-8">
      <button
        onClick={() => setSelectedView('overview')}
        className={`px-6 py-3 rounded-lg font-medium transition-all ${
          selectedView === 'overview'
            ? 'bg-blue-600 text-white shadow-lg'
            : 'bg-white dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
        }`}
      >
        <DocumentChartBarIcon className="h-5 w-5 inline mr-2" />
        Overview
      </button>
      <button
        onClick={() => setSelectedView('details')}
        className={`px-6 py-3 rounded-lg font-medium transition-all ${
          selectedView === 'details'
            ? 'bg-blue-600 text-white shadow-lg'
            : 'bg-white dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
        }`}
      >
        <ChartBarIcon className="h-5 w-5 inline mr-2" />
        All Scores
      </button>
      <button
        onClick={() => setSelectedView('simulator')}
        className={`px-6 py-3 rounded-lg font-medium transition-all relative ${
          selectedView === 'simulator'
            ? 'bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow-lg'
            : 'bg-white dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
        }`}
      >
        <BeakerIcon className="h-5 w-5 inline mr-2" />
        SmartSim‚Ñ¢
        {!simulatorEnabled && (
          <span className="absolute -top-2 -right-2 px-2 py-1 bg-yellow-500 text-white text-xs rounded-full">
            Coming Soon
          </span>
        )}
      </button>
    </div>
  );

  // Overview cards with all score models
  const ScoreModelCards = () => {
    const scoreData = getScoresByModel();
    return (
      <div className="space-y-6">
        {Object.entries(scoreModels).map(([modelKey, modelInfo]) => {
          const modelScores = scoreData[modelKey];
          const hasData = Object.values(modelScores).some(bureau => bureau.current !== null);
          return (
            <motion.div
              key={modelKey}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className={`bg-white dark:bg-gray-800 rounded-xl p-6 shadow-lg ${
                !hasData ? 'opacity-60' : ''
              }`}
            >
              <div className="flex justify-between items-start mb-4">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                    {modelInfo.name}
                  </h3>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    {modelInfo.usage}
                  </p>
                  <span className="text-xs text-gray-500">
                    Range: {modelInfo.range}
                  </span>
                </div>
                {!hasData && (
                  <span className="px-3 py-1 bg-gray-100 dark:bg-gray-700 text-gray-600 dark:text-gray-400 rounded-full text-sm">
                    Data not available
                  </span>
                )}
              </div>
              <div className="grid grid-cols-3 gap-4">
                {['Equifax', 'Experian', 'TransUnion'].map(bureau => {
                  const bureauScore = modelScores[bureau];
                  return (
                    <div key={bureau} className="text-center">
                      <p className="text-xs text-gray-600 dark:text-gray-400 mb-1">
                        {bureau}
                      </p>
                      <p className="text-2xl font-bold text-gray-900 dark:text-white">
                        {bureauScore.current || '---'}
                      </p>
                      {bureauScore.change !== 0 && (
                        <div className={`text-xs mt-1 ${
                          bureauScore.change > 0 ? 'text-green-600' : 'text-red-600'
                        }`}>
                          {bureauScore.change > 0 ? '+' : ''}{bureauScore.change}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            </motion.div>
          );
        })}
      </div>
    );
  };

  // SmartSim‚Ñ¢ Simulator placeholder
  const SmartSimulator = () => (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="bg-gradient-to-r from-purple-50 to-pink-50 dark:from-purple-900/20 dark:to-pink-900/20 rounded-xl p-8 text-center"
    >
      <BeakerIcon className="h-16 w-16 text-purple-600 dark:text-purple-400 mx-auto mb-4" />
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
        SmartSim‚Ñ¢ Credit Score Simulator
      </h2>
      <p className="text-gray-700 dark:text-gray-300 mb-6 max-w-2xl mx-auto">
        Coming soon! Test &quot;what-if&quot; scenarios to see how different actions could impact your credit scores. 
        Pay down debt, remove collections, or add new accounts to see projected score changes.
      </p>
      {!simulatorEnabled ? (
        <div className="inline-flex items-center gap-2 px-4 py-2 bg-yellow-100 dark:bg-yellow-900/30 text-yellow-700 dark:text-yellow-300 rounded-lg">
          <LockClosedIcon className="h-5 w-5" />
          <span>Available in Premium Plans</span>
        </div>
      ) : (
        <button className="px-6 py-3 bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-lg hover:shadow-lg transition-shadow">
          <SparklesIcon className="h-5 w-5 inline mr-2" />
          Launch Simulator
        </button>
      )}
    </motion.div>
  );

  // Monthly AI Report placeholder
  const AIReportSection = () => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="mt-8 bg-gradient-to-r from-blue-50 to-green-50 dark:from-blue-900/20 dark:to-green-900/20 rounded-xl p-6"
    >
      <div className="flex items-center gap-3 mb-4">
        <SparklesIcon className="h-6 w-6 text-blue-600 dark:text-blue-400" />
        <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
          AI Monthly Intelligence Report
        </h3>
        <span className="px-2 py-1 bg-green-100 dark:bg-green-900/30 text-green-700 dark:text-green-300 rounded-full text-xs">
          New Feature
        </span>
      </div>
      <p className="text-gray-700 dark:text-gray-300 mb-4">
        Your personalized credit analysis will appear here each month, explaining changes and providing actionable recommendations.
      </p>
      <button className="text-blue-600 hover:text-blue-700 dark:text-blue-400 text-sm font-medium">
        Learn More ‚Üí
      </button>
    </motion.div>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <ScoreViewHeader />
      <EducationalBanner />
      <ViewTabs />
      {selectedView === 'overview' && (
        <>
          <ScoreModelCards />
          <AIReportSection />
        </>
      )}
      {selectedView === 'details' && (
        <div className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-lg">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
            Detailed Score History
          </h2>
          {/* Add detailed charts and history here */}
          <p className="text-gray-600 dark:text-gray-400">
            Full score history and trending analysis coming soon...
          </p>
        </div>
      )}
      {selectedView === 'simulator' && <SmartSimulator />}
    </div>
  );
};

export default ClientScores;

import React, { useEffect, useState } from 'react';
import { db } from '@/lib/firebase';
import { collection, onSnapshot } from 'firebase/firestore';
import { Bar, Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const ContactReports = () => {
  const [contacts, setContacts] = useState([]);

  useEffect(() => {
    const unsub = onSnapshot(collection(db, 'contacts'), (snap) => {
      setContacts(snap.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    });
    return () => unsub();
  }, []);

  // Example chart data
  const weeklyData = {
    labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
    datasets: [{ label: 'Contacts Added', data: [5, 8, 6, 10, 7, 4, 3], backgroundColor: 'rgba(59,130,246,0.5)' }]
  };
  const conversionData = {
    labels: ['Lead', 'Prospect', 'Client'],
    datasets: [{ label: 'Conversions', data: [30, 15, 8], backgroundColor: ['#fbbf24', '#34d399', '#3b82f6'] }]
  };

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-6">Contact Reports & Analytics</h1>
      <div className="mb-8">
        <h2 className="text-lg font-semibold mb-2">Weekly Contact Summary</h2>
        <Bar data={weeklyData} />
      </div>
      <div className="mb-8">
        <h2 className="text-lg font-semibold mb-2">Conversion Analysis</h2>
        <Bar data={conversionData} />
      </div>
      <div className="mb-8">
        <h2 className="text-lg font-semibold mb-2">Contact Trends</h2>
        <Line data={weeklyData} />
      </div>
      {/* ...more charts and metrics... */}
    </div>
  );
};

export default ContactReports;

import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { useAuth } from '@/auth/AuthProvider';
import { doc, getDoc, collection, query, where, orderBy, onSnapshot, getDocs } from 'firebase/firestore';
import { db } from '../lib/firebase';
import { 
  ChartBarIcon, 
  DocumentTextIcon,
  FolderIcon,
  BellIcon,
  ArrowTrendingUpIcon
} from '@heroicons/react/24/outline';
import BrandLogo from "@/components/BrandLogo";
import { useTheme } from '@/theme/ThemeProvider';

// --- ON-SCREEN BEACON ---
const Beacon = () => (
  <div style={{background:'#e0e7ff',color:'#3730a3',padding:'4px 0',textAlign:'center',fontWeight:'bold',letterSpacing:1,marginBottom:12,zIndex:9999}}>
    [BEACON: Dashboard.jsx]
  </div>
);

const quickActions = [
  { label: 'Credit Scores', href: '/client/reports', color: 'bg-blue-600' },
  { label: 'Dispute Letters', href: '/client/disputes', color: 'bg-yellow-500' },
  { label: 'Client Management', href: '/contacts', color: 'bg-green-600' },
  { label: 'Client Portal', href: '/client', color: 'bg-purple-600' },
];

export default function Dashboard() {
  const { user } = useAuth();
  const { theme, setTheme } = useTheme();
  const [clientData, setClientData] = useState(null);
  const [creditScores, setCreditScores] = useState([]);
  const [disputes, setDisputes] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [score, setScore] = useState(null);
  const [activity, setActivity] = useState([]);

  useEffect(() => {
    if (!user) return;

    const loadClientData = async () => {
      try {
        const clientDoc = await getDoc(doc(db, 'clients', user.uid));
        if (clientDoc.exists()) {
          setClientData(clientDoc.data());
        } else {
          const q = query(collection(db, 'contacts'), where('email', '==', user.email));
          const snapshot = await getDocs(q);
          if (!snapshot.empty) {
            setClientData(snapshot.docs[0].data());
          }
        }
      } catch (error) {
        console.error('Error loading client data:', error);
      }
    };

    const scoresQuery = query(
      collection(db, 'creditScores'),
      where('clientEmail', '==', user.email),
      orderBy('date', 'desc')
    );
    const unsubscribeScores = onSnapshot(scoresQuery, (snapshot) => {
      const scoresData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setCreditScores(scoresData);
    });

    const disputesQuery = query(
      collection(db, 'disputeLetters'),
      where('recipientEmail', '==', user.email),
      orderBy('createdAt', 'desc')
    );
    const unsubscribeDisputes = onSnapshot(disputesQuery, (snapshot) => {
      const disputesData = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setDisputes(disputesData);
    });

    loadClientData();
    setLoading(false);

    return () => {
      unsubscribeScores();
      unsubscribeDisputes();
    };
  }, [user]);

  useEffect(() => {
    if (creditScores.length > 0) {
      setScore(creditScores[0]?.score);
    }
  }, [creditScores]);

  useEffect(() => {
    if (disputes.length > 0) {
      const recentActivity = disputes.map(dispute => ({
        type: 'dispute',
        status: dispute.status,
        bureau: dispute.bureau
      }));
      setActivity(recentActivity);
    }
  }, [disputes]);

  const getScoreImprovement = () => {
    if (creditScores.length < 2) return 0;
    const latest = creditScores[0]?.score || 0;
    const previous = creditScores[1]?.score || 0;
    return latest - previous;
  };

  const stats = [
    {
      title: 'Current Credit Score',
      value: creditScores[0]?.score || '---',
      change: getScoreImprovement(),
      icon: ChartBarIcon,
      color: 'blue'
    },
    {
      title: 'Active Disputes',
      value: disputes.filter(d => d.status !== 'resolved').length,
      icon: DocumentTextIcon,
      color: 'green'
    },
    {
      title: 'Documents',
      value: documents.length,
      icon: FolderIcon,
      color: 'purple'
    }
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {import.meta.env.DEV && <Beacon />}
      <header className="flex items-center justify-between p-4 bg-white dark:bg-gray-800 shadow mb-6">
        <BrandLogo portalType="admin" theme={theme === 'dark' ? 'dark' : 'default'} style={{height:48}} />
        <button
          className="ml-4 px-2 py-1 rounded bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200"
          onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
          aria-label="Toggle theme"
        >
          {theme === 'dark' ? 'üåô' : '‚òÄÔ∏è'}
        </button>
      </header>

      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Header with logo and theme toggle */}
        <div className="flex items-center justify-between mb-8">
          <img
            src={window.matchMedia('(prefers-color-scheme: dark)').matches ? logoDark : logoLight}
            alt="Speedy Credit Repair Logo"
            className="h-10 md:h-10"
            style={{ maxHeight: 40 }}
          />
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-gradient-to-r from-blue-600 to-green-600 rounded-xl p-8 mb-8 text-white"
        >
          <h1 className="text-3xl font-bold mb-2">
            Welcome back, {clientData?.firstName || user?.displayName || user?.email?.split('@')[0] || 'Client'}!
          </h1>
          <p className="text-xl opacity-90">
            Track your credit repair journey and monitor your progress
          </p>
          {getScoreImprovement() > 0 && (
            <div className="mt-4 inline-flex items-center bg-white/20 backdrop-blur rounded-lg px-4 py-2">
              <ArrowTrendingUpIcon className="h-5 w-5 mr-2" />
              <span>Your score improved by {getScoreImprovement()} points!</span>
            </div>
          )}
        </motion.div>

        {/* Quick Actions */}
        <div className="grid grid-cols-2 gap-4 mb-8">
          {quickActions.map((action) => (
            <a
              key={action.label}
              href={action.href}
              className={`rounded-lg p-4 text-white font-semibold text-center shadow transition-colors ${action.color} hover:opacity-90 dark:bg-opacity-80`}
            >
              {action.label}
            </a>
          ))}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          {stats.map((stat, index) => (
            <motion.div
              key={stat.title}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-lg"
            >
              <div className="flex items-center justify-between mb-4">
                <div className={`p-3 rounded-lg bg-${stat.color}-100 dark:bg-${stat.color}-900/30`}>
                  <stat.icon className={`h-6 w-6 text-${stat.color}-600 dark:text-${stat.color}-400`} />
                </div>
                {stat.change > 0 && (
                  <span className="text-green-600 text-sm font-semibold">
                    +{stat.change}
                  </span>
                )}
              </div>
              <div className="text-2xl font-bold text-gray-900 dark:text-white mb-1">
                {stat.value}
              </div>
              <div className="text-sm text-gray-600 dark:text-gray-400">
                {stat.title}
              </div>
            </motion.div>
          ))}
        </div>

        {/* Credit Score Card */}
        <div className="mb-6 bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <span className="font-semibold text-gray-700 dark:text-gray-200">Credit Score:</span> <span className="text-xl font-bold text-blue-700 dark:text-blue-300">{score ?? 'N/A'}</span>
        </div>

        {/* Recent Activity */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
          <h2 className="text-lg font-semibold mb-2 text-gray-800 dark:text-gray-100">Recent Activity</h2>
          {activity.length === 0 ? <div className="text-gray-500 dark:text-gray-400">No recent activity.</div> : (
            <ul>
              {activity.map((item, i) => (
                <li key={i} className="mb-1 text-gray-700 dark:text-gray-200">
                  {item.type === 'dispute' ? `Dispute: ${item.status}` : `Document: ${item.name}`}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from "react";
import { db } from "../lib/firebase";
import { collection, addDoc, getDocs, updateDoc, doc } from "firebase/firestore";
import ContractEditor from "../components/ContractEditor";

const EContracts = () => {
  const [contracts, setContracts] = useState([]);
  const [showEditor, setShowEditor] = useState(false);
  const [selectedContract, setSelectedContract] = useState(null);

  useEffect(() => {
    const fetchContracts = async () => {
      const querySnapshot = await getDocs(collection(db, "contracts"));
      setContracts(querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    };
    fetchContracts();
  }, []);

  return (
    <div className="max-w-4xl mx-auto py-8 px-4">
      <h1 className="text-2xl font-bold mb-4">E-Contracts & Document Completion</h1>
      <button className="mb-4 px-4 py-2 bg-blue-600 text-white rounded" onClick={() => { setShowEditor(true); setSelectedContract(null); }}>New Contract</button>
      <div className="space-y-4">
        {contracts.map(contract => (
          <div key={contract.id} className="p-4 bg-white rounded shadow flex justify-between items-center">
            <div>
              <div className="font-semibold">{contract.title}</div>
              <div className="text-xs text-gray-500">Status: {contract.status}</div>
            </div>
            <button className="text-blue-600 underline" onClick={() => { setShowEditor(true); setSelectedContract(contract); }}>View/Edit</button>
          </div>
        ))}
      </div>
      {showEditor && (
        <ContractEditor contract={selectedContract} onClose={() => setShowEditor(false)} />
      )}
    </div>
  );
};

export default EContracts;

import React from "react";
